import React, { useState } from 'react';
const api = {
  key : "0dbba878a16119c3c8b596c58e17ffb4",
  base: "https://api.openweathermap.org/data/2.5/"
}

function App() {

  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      //When the key enter is hit: a GET request is fired off to request and receive a json package of weather information from openweather api
      fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
      .then(res => res.json())
      .then(result => {
        setWeather(result)
        setQuery('');
        console.log(result);

      });

    }
  }


  //Function to build a date containing a month, date and year:
  const dateBuilder = (d) => {
    let months = ["January", "Feburary", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    //extracting the current date and time using built-in function:
    let day = days[d.getDay()];
    let date = d.getDate(); 
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day} ${date} ${month} ${year}`



  }

  return (
    <div className="app">
      <main>
        <div className="search-box">
          <input 
            type="text"
            className="search-bar"
            placeholder="Search..."
            onChange={e => setQuery(e.target.value)}
            value={query}
            onKeyPress={search}
          />
        </div>
        {(typeof weather.main != "undefined") ? ( 
          <div>
            <div className="location-box">
              <div className="location">
                {weather.name}, {weather.sys.country}
              </div>
              <div className="weather-box">
                <div className="temp">
                  15*c
                </div>
                <div className="weather">
                  Sunny
                </div>
              </div>
              <div className="date">
                {dateBuilder(new Date())}
              </div>

            </div>
          </div>

        ) : ('')}
      </main>
    </div>
  );
}

export default App;
